// 						<Route exact path="/orderHistory" component={OrderHistory} />
// 						<Route exact path="/products/:id" component={Detail} />
// 						<Route component={NoMatch} />
// 					</Switch>
// 				</div>
// 			</Router>
// 		</ApolloProvider>
// 	);
// }

function App() {
  return (
    <ApolloProvider client={client}>
      <Router>
        <div>
          <StoreProvider>
            <Nav />
            <Routes>
              <Route 
                path="/" 
                element={<Home />} 
              />
              <Route 
                path="/login" 
                element={<Login />}
              />
              <Route 
                path="/signup" 
                element={<Signup />} 
              />
              <Route 
                path="/orderHistory" 
                element={<OrderHistory />} 
              />
              <Route 
                path="/products/:id" 
                element={<Detail />}
              />
              <Route 
                path="*" 
                element={<NoMatch />}
              />
            </Routes>
          </StoreProvider>
        </div>
      </Router>
    </ApolloProvider>
  );
}

export default App;
